#!/bin/zsh
# SwiftBar plugin to show Slack notification count

count=$(osascript -e '
tell application "System Events"
	tell process "Dock"
			repeat with i from 1 to count of UI elements of list 1
					set d to UI element i of list 1
					if (description of d is "application dock item") and (name of d is "Slack") then
							try
									return value of attribute "AXStatusLabel" of d
							end try
					end if
			end repeat
	end tell
end tell'
)

if [[ "$count" == "missing value" ]]; then
	count=""
fi

echo "$count | color=#FF3B30 "


